function [errcfs] = voteWeight(clslabels, tstl, eff)% simple majority classifier - winning class must have 50% + 1 votes% clslabels - matrix containing elementary classifiers results%	each row contains NN labels for one test element% tstl - test set labels (ground truth)% errcf - error coefficients of the classifier# eff - efficiency matrix. Contains efficiencies of each individual classificator#The idea is to take into acount individual classifier efficiency# So the vote matters the most for better classifierclslabels = repmat(clslabels,1,7);#takes the best[max_values indices] = max(eff);n = 2; # number of votesscale = repmat(clslabels(:,indices),1,n);clslabels = horzcat(clslabels,scale);	clsCount = columns(clslabels);	treshold = floor(clsCount/2) + 1;	reject = max(tstl) + 1; 	[lab f] = mode(clslabels');	for i=1:size(clslabels,1)		if (f(i) < treshold)			res = clslabels(i,:);			res(res == lab(i)) = [];			[l2 f2] = mode(res);			if f2 == f(i)				lab(i) = reject;			end		end	end		confmx = confMx(tstl, lab);	errcfs = compErrors(confmx);